// AuthHandler.js
import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';

const AuthHandler = () => {
    const history = useHistory();

    useEffect(() => {
        // Extract token from URL
        const urlParams = new URLSearchParams(window.location.search);
        const token = urlParams.get('token');
        const userEmail = urlParams.get('email'); // Assuming the email is passed in the URL
        const userName = urlParams.get('name');   // Assuming the name is passed in the URL

        if (token) {
            // Store the token in localStorage
            localStorage.setItem('bearerToken', token);

            // Store the user's email and name in localStorage
            if (userEmail) localStorage.setItem('userEmail', userEmail);
            if (userName) localStorage.setItem('userName', userName);

            // Redirect to the main application page (e.g., Onebox)
            history.push('/onebox');
        } else {
            // Handle error if token is missing
            alert('Login failed. No token received.');
        }
    }, [history]);

    return <div>Logging you in...</div>;
};

export default AuthHandler;
