// src/components/Onebox.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EmailList from './EmailList';
import EmailDetails from './EmailDetails';
import ReplyEditor from './ReplyEditor';
import DarkModeToggle from './DarkModeToggle';

const Onebox = () => {
  const [emails, setEmails] = useState([]);
  const [selectedEmail, setSelectedEmail] = useState(null);
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    axios.get('https://hiring.reachinbox.xyz/api/v1/onebox/list', {
      headers: {
        Authorization: `Bearer YOUR_TOKEN_HERE`,
      },
    })
      .then(response => {
        console.log('Emails fetched:', response.data.data); // Debugging
        setEmails(response.data.data);
      })
      .catch(error => console.error('Error fetching emails:', error));
  }, []);

  const handleDelete = (threadId) => {
    axios.delete(`https://hiring.reachinbox.xyz/api/v1/onebox/messages/${threadId}`, {
      headers: {
        Authorization: `Bearer YOUR_TOKEN_HERE`,
      },
    })
      .then(() => {
        setEmails(emails.filter(email => email.threadId !== threadId));
        setSelectedEmail(null);
      })
      .catch(error => console.error('Error deleting email:', error));
  };

  const handleReply = (replyData) => {
    axios.post(`https://hiring.reachinbox.xyz/api/v1/onebox/messages/${selectedEmail.threadId}`, replyData, {
      headers: {
        Authorization: `Bearer YOUR_TOKEN_HERE`,
      },
    })
      .then(() => alert('Reply sent!'))
      .catch(error => console.error('Error sending reply:', error));
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={darkMode ? 'onebox-container dark-mode' : 'onebox-container'}>
      <DarkModeToggle toggleDarkMode={toggleDarkMode} />
      <EmailList emails={emails} setSelectedEmail={setSelectedEmail} />
      <EmailDetails email={selectedEmail} />
      {selectedEmail && <ReplyEditor onReply={handleReply} />}
    </div>
  );
};

export default Onebox;
