import React, { useState, useEffect } from 'react';
import './Onebox.css';

const Onebox = () => {
    const [selectedEmail, setSelectedEmail] = useState(null);
    const [darkMode, setDarkMode] = useState(false);
    const [logo, setLogo] = useState('/icons/logo-light.png');
    const [toggleIcon, setToggleIcon] = useState('/icons/light-toggle-icon.png');

    useEffect(() => {
        if (darkMode) {
            setLogo('/icons/logo-dark.png');
            setToggleIcon('/icons/dark-toggle-icon.png');
        } else {
            setLogo('/icons/logo-light.png');
            setToggleIcon('/icons/light-toggle-icon.png');
        }
    }, [darkMode]);

    const handleSelectEmail = (email) => {
        setSelectedEmail(email);
    };

    const handleSendReply = () => {
        setSelectedEmail(null);
    };

    return (
        <div className={`onebox-container ${darkMode ? 'dark' : 'light'}`}>
            <div className="sidebar">
                <a href="/" className="logo-link">
                    <img src={logo} alt="Logo" className="sidebar-logo" />
                </a>
                <ul className="sidebar-menu">
                    <li>
                        <a href="/home">
                            <img src="/icons/home-icon.png" alt="Home" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/inbox">
                            <img src="/icons/email-icon.png" alt="Inbox" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/sent">
                            <img src="/icons/send-icon.png" alt="Sent" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/drafts">
                            <img src="/icons/drafts-icon.png" alt="Drafts" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/trash">
                            <img src="/icons/trash-icon.png" alt="Trash" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/contacts">
                            <img src="/icons/contacts-icon.png" alt="Contacts" className="icon" />
                        </a>
                    </li>
                    <li>
                        <a href="/analytics">
                            <img src="/icons/analytics-icon.png" alt="Analytics" className="icon" />
                        </a>
                    </li>
                </ul>
                <div className="profile-section">
                    <img src="/icons/profile-icon.png" alt="Profile" className="profile-icon" />
                </div>
            </div>
            <div className="main-content">
                <div className="top-bar">
                    <div className="workspace-title">Onebox</div>
                    <div className="dark-mode-toggle" onClick={() => setDarkMode(!darkMode)}>
                        <img src={toggleIcon} alt="Toggle Dark Mode" className="toggle-icon" />
                    </div>
                    <div className="workspace-name">Tim's Workspace</div>
                </div>
                <div className="content">
                    {selectedEmail ? (
                        <>
                            <div>Email Details Component Placeholder</div>
                            <div>Reply Editor Component Placeholder</div>
                        </>
                    ) : (
                        <div className="no-email-selected">
                            <img src="/icons/inbox-empty.png" alt="No Emails" className="empty-inbox-image" />
                            <p>It's the beginning of a legendary sales pipeline</p>
                            <p>When you have inbound Emails you'll see them here</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Onebox;
