// src/components/EmailDetails.js
import React from 'react';

const EmailDetails = ({ email }) => {
  return (
    <div className="email-details">
      {email ? (
        <div>
          <h2>{email.subject}</h2>
          <p>{email.body}</p>
        </div>
      ) : (
        <p>Select an email to view details</p>
      )}
    </div>
  );
};

export default EmailDetails;
