// src/components/ReplyEditor.js
import React, { useState } from 'react';
import CustomTextEditor from './CustomTextEditor';

const ReplyEditor = ({ onReply }) => {
  const [reply, setReply] = useState({ subject: '', body: '' });

  const handleSend = () => {
    onReply(reply);
  };

  return (
    <div className="reply-editor">
      <input
        type="text"
        placeholder="Subject"
        value={reply.subject}
        onChange={(e) => setReply({ ...reply, subject: e.target.value })}
      />
      <CustomTextEditor value={reply.body} onChange={(body) => setReply({ ...reply, body })} />
      <button onClick={handleSend}>Send</button>
    </div>
  );
};

export default ReplyEditor;
