import React, { useState } from 'react';
import './Onebox.css';

const Onebox = () => {
    const [selectedEmail, setSelectedEmail] = useState(null);
    const [darkMode, setDarkMode] = useState(false);

    const handleSelectEmail = (email) => {
        setSelectedEmail(email);
    };

    const handleSendReply = () => {
        setSelectedEmail(null);
    };

    return (
        <div className={`onebox-container ${darkMode ? 'dark' : 'light'}`}>
            <div className="sidebar">
                <img src="/icons/logo-light.png" alt="Logo" className="sidebar-logo" />
                <nav className="sidebar-menu">
                    <ul>
                        <li><img src="/icons/home-icon.png" alt="Home" className="icon" /></li>
                        <li><img src="/icons/email-icon.png" alt="Inbox" className="icon" /></li>
                        <li><img src="/icons/send-icon.png" alt="Sent" className="icon" /></li>
                        <li><img src="/icons/drafts-icon.png" alt="Drafts" className="icon" /></li>
                        <li><img src="/icons/trash-icon.png" alt="Trash" className="icon" /></li>
                        <li><img src="/icons/contacts-icon.png" alt="Contacts" className="icon" /></li>
                        <li><img src="/icons/analytics-icon.png" alt="Analytics" className="icon" /></li>
                    </ul>
                </nav>
                <div className="profile-section">
                    <img src="/icons/profile-icon.png" alt="Profile" className="profile-icon" />
                </div>
            </div>

            <div className="main-content">
                <div className="top-bar">
                    <div className="workspace-title">Onebox</div>
                    <div className="dark-mode-toggle">
                        <img 
                            src={darkMode ? "/icons/dark-toggle-icon.png" : "/icons/light-toggle-icon.png"} 
                            alt="Toggle" 
                            className="toggle-icon" 
                            onClick={() => setDarkMode(!darkMode)} 
                        />
                    </div>
                    <div className="workspace-name">Tim's Workspace</div>
                </div>

                <div className="content">
                    {selectedEmail ? (
                        <>
                            <div>Email Details</div>
                            <div>Reply Editor</div>
                        </>
                    ) : (
                        <div className="no-email-selected">
                            <img src="/icons/inbox-empty.png" alt="No Emails" />
                            <p>It's the beginning of a legendary sales pipeline</p>
                            <p>When you have inbound Emails you'll see them here</p>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Onebox;
